	public String convertBytesToString(byte[] bytes) throws PrivacityException {
		
		if (bytes==null) return null;
		try {
			return new String(bytes, "UTF-8");
		} catch (UnsupportedEncodingException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		return null;
		
	}
	public byte[] compress(byte[] encryp) throws PrivacityException {
		byte[] compressed;
		try {
			compressed = new ZipUtil().compress(encryp);
		} catch (IOException e) {
			e.printStackTrace();
			throw new PrivacityException(ExceptionReturnCode.ZIP_COMPRESS);
		}
		return compressed;
	}
	
	public byte[] decompress(byte[] decryp) throws PrivacityException {
		byte[] decompress;
		try {
			decompress = new ZipUtil().decompress(decryp);
		} catch (IOException e) {
			e.printStackTrace();
			log.error(ExceptionReturnCode.ZIP_DECOMPRESS.toShow(e));
			throw new PrivacityException(ExceptionReturnCode.ZIP_DECOMPRESS);
		}
		return decompress;
	}
	
	public String gsonToSendCompress(Object s) throws PrivacityException {
		if (s==null)return null;
		String s1 = gsonToSend(s);

		return compressB64(s1);
	}


	public String gsonToSend(Object s) {
		if (s==null)return null;
		
		ObjectMapper mapper = new ObjectMapper();
	  	  String dtoAsString=null;
		try {
			dtoAsString = mapper.writeValueAsString(s);
		} catch (JsonProcessingException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
			
			//throw new PrivacityException(ExceptionReturnCode.GENERAL_INVALID_SENT_DATA);
		}
	  	dtoAsString= replacing(dtoAsString);
		return dtoAsString;
	}	

	public final Gson gson() {
		return new GsonBuilder()
				.registerTypeAdapter(LocalDateTime.class, new LocalDateAdapter())
				//.excludeFieldsWithoutExposeAnnotation()
				.excludeFieldsWithModifiers(Modifier.STATIC)
				.serializeNulls()
				.create();

		
	}
	public final Gson gsonPretty() {
		return new GsonBuilder()
				.setPrettyPrinting()
				.registerTypeAdapter(LocalDateTime.class, new LocalDateAdapter())
				//.excludeFieldsWithoutExposeAnnotation()
				.excludeFieldsWithModifiers(Modifier.STATIC)
				.serializeNulls()
				.create();

		
	}
	public final String replacing(String s) {
		if (s == null) return s;

		String r = s.toString().replace("\n", "").replace("\t", "").replace("\r", "").replace("\b", "").replace("\f", "")
				.replace("  ", " ").replace("  ", " ").replace("  ", " ").replace(", " , ",");

		return r;
	}
	
	private final String replacingToShow(String s) {
		if (s == null) return s;

		String r = s.toString().replace("\n", "").replace("\t", "").replace("\r", "").replace("\b", "").replace("\f", "")
				.replace("  ", " ").replace("  ", " ").replace("  ", " ").replace(", " , "");

		return r;
	}

	public final Object clon(Class<?> clazz, Object o ) {
		
	       
        String j = gson().toJson(o);
        
        Object fromJson = gson().fromJson(j, clazz);
		return fromJson;
		
	}
	
	
